sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {

	},

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		// Create GlideDateTime objects for the currnet date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// The current date
		var todaysDate = rightnow.getLocalDate();
		// The date for When needed
		var testCompareDate = testdate.getLocalDate();
		// If the When needed date is today, do not write the records to the database output an
		// error message to the screen.
		if(testCompareDate.compareTo(todaysDate) == 0) {
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
